import axios from "axios"

export const getMovieList = async () => {
    // Panggil API untuk mendapatkan daftar movie yang populer
    const movie = await axios.get(`${process.env.REACT_APP_BASEURL}/movie/popular?api_key${process.env.REACT_APP_APIKEY}`);

    // Tampilkan daftar movie ke console
    console.log({ movieList: movie });

    // Kembalikan hasil dari pemanggilan API
    return movie;
}

export const searchMovie = async (q) => {
    // Panggil API untuk mencari movie sesuai dengan parameter q
    const search = await axios.get(q)
    // const search = await axios.get(`${process.env.REACT_APP_BASEURL}/search/movie?query=${q}`);

    // Kembalikan hasil dari pemanggilan API
    return search;
}
