  const [popularMovies, setPopularMovies] = useState([])
  useEffect(() => {
    getMovieList().then((result) => {
      setPopularMovies(result)
    })
  }, [])
  const PopularMoviesList = () => {
    return popularMovies.map((movie, i) => {
      return (
        <div key={i}>
          <div>{movie.title}</div>
          <div>{movie.poster_path}</div>
          <div>{movie.title}</div>
          <div>{movie.title}</div>
        </div>
      )
    })
  }
